import React, { useState, useEffect } from 'react';
import './App.css';
import Title from './components/Title/Title';
import Button from './components/Button/Button';
import Card from './components/Card/Card';

// Symboles pour les cartes
const cardSymbols = ['ðŸŽ®', 'ðŸŽ¯', 'ðŸŽ¨', 'ðŸŽ­', 'ðŸŽª', 'ðŸŽ¸', 'ðŸŽ²', 'ðŸŽ°'];

function App() {
  const [cards, setCards] = useState([]);
  const [flippedCards, setFlippedCards] = useState([]);
  const [matchedPairs, setMatchedPairs] = useState([]);
  const [moves, setMoves] = useState(0);
  const [gameWon, setGameWon] = useState(false);
  const [timer, setTimer] = useState(0);
  const [isTimerRunning, setIsTimerRunning] = useState(false);

  // Initialiser le jeu
  useEffect(() => {
    initializeGame();
  }, []);

  // Timer
  useEffect(() => {
    let interval = null;
    if (isTimerRunning) {
      interval = setInterval(() => {
        setTimer((prevTimer) => prevTimer + 1);
      }, 1000);
    } else {
      clearInterval(interval);
    }
    return () => clearInterval(interval);
  }, [isTimerRunning]);

  // Initialiser ou rÃ©initialiser le jeu
  const initializeGame = () => {
    // CrÃ©er les paires de cartes
    const shuffledCards = [...cardSymbols, ...cardSymbols]
      .sort(() => Math.random() - 0.5)
      .map((value, index) => ({
        id: index,
        value: value,
      }));

    setCards(shuffledCards);
    setFlippedCards([]);
    setMatchedPairs([]);
    setMoves(0);
    setGameWon(false);
    setTimer(0);
    setIsTimerRunning(false);
  };

  // GÃ©rer le clic sur une carte
  const handleCardClick = (card) => {
    // DÃ©marrer le timer au premier clic
    if (moves === 0 && !isTimerRunning) {
      setIsTimerRunning(true);
    }

    // Ne rien faire si deux cartes sont dÃ©jÃ  retournÃ©es
    if (flippedCards.length === 2) {
      return;
    }

    // Ne rien faire si la carte est dÃ©jÃ  retournÃ©e
    if (flippedCards.find((c) => c.id === card.id)) {
      return;
    }

    const newFlippedCards = [...flippedCards, card];
    setFlippedCards(newFlippedCards);

    // Si deux cartes sont retournÃ©es
    if (newFlippedCards.length === 2) {
      setMoves(moves + 1);

      // VÃ©rifier si les cartes correspondent
      if (newFlippedCards[0].value === newFlippedCards[1].value) {
        // Paire trouvÃ©e
        const newMatchedPairs = [...matchedPairs, newFlippedCards[0].value];
        setMatchedPairs(newMatchedPairs);
        setFlippedCards([]);

        // VÃ©rifier si le jeu est gagnÃ©
        if (newMatchedPairs.length === cardSymbols.length) {
          setGameWon(true);
          setIsTimerRunning(false);
        }
      } else {
        // Pas de correspondance, retourner les cartes aprÃ¨s un dÃ©lai
        setTimeout(() => {
          setFlippedCards([]);
        }, 1000);
      }
    }
  };

  // Formater le temps
  const formatTime = (seconds) => {
    const mins = Math.floor(seconds / 60);
    const secs = seconds % 60;
    return `${mins.toString().padStart(2, '0')}:${secs.toString().padStart(2, '0')}`;
  };

  return (
    <div className="App">
      <Title text="Memory Game" />
      
      <div className="game-info">
        <div className="info-item">
          <span className="info-label">Coups:</span>
          <span className="info-value">{moves}</span>
        </div>
        <div className="info-item">
          <span className="info-label">Temps:</span>
          <span className="info-value">{formatTime(timer)}</span>
        </div>
        <div className="info-item">
          <span className="info-label">Paires:</span>
          <span className="info-value">{matchedPairs.length}/{cardSymbols.length}</span>
        </div>
      </div>

      <div className="cards-grid">
        {cards.map((card) => (
          <Card
            key={card.id}
            card={card}
            handleCardClick={handleCardClick}
            isFlipped={flippedCards.some((c) => c.id === card.id)}
            isMatched={matchedPairs.includes(card.value)}
          />
        ))}
      </div>

      {gameWon && (
        <div className="victory-message">
          <h2>ðŸŽ‰ FÃ©licitations ! ðŸŽ‰</h2>
          <p>Vous avez gagnÃ© en {moves} coups et {formatTime(timer)} !</p>
        </div>
      )}

      <div className="button-container">
        <Button text="Nouvelle Partie" onClick={initializeGame} />
      </div>
    </div>
  );
}

export default App;
